        "title": "[í”„ë¡œê·¸ë˜ë¨¸ìŠ¤] [1ì°¨] ì…”í‹€ë²„ìŠ¤ (python)",
        "excerpt":"ë¬¸ì œ   https://programmers.co.kr/learn/courses/30/lessons/17678   í’€ì´   1. ì²˜ìŒ ë§ì¶”ê¸° ìœ„í•´ ì‘ì„±í•œ ì½”ë“œ   from collections import deque  # ì…”í‹€ ë²„ìŠ¤ ì‹œê°„ ì–´ë ˆì´(ë¶„ ë‹¨ìœ„ ex) 09:00 =&gt; 540) def nt_to_arr(n, t):     deptart_times = []     for i in range(n):         deptart_times.append(540+i*t)     return deptart_times # print(nt_to_arr(1, 10))   # ë¬¸ìì—´ ì‹œê°„ =&gt; ë¶„ë‹¨ìœ„ ì‹œê°„ ë³€í™˜ def timetable_to_arr(timetable):     arrive_times = []          for stamp in timetable:         hh, mm = stamp.split(\":\")         arrive_times.append(60*int(hh) + int(mm))      return arrive_times # print(timetable_to_arr([\"08:00\", \"08:01\", \"08:02\", \"08:03\"]))   # ë¶„ë‹¨ìœ„ ì‹œê°„ =&gt; ë¬¸ìì—´ ì‹œê°„ ë³€í™˜ def mtime_to_hhmm(minute):     hh, mm = divmod(minute, 60)      hh = str(hh) if hh &gt;= 10 else \"0\"+str(hh)     mm = str(mm) if mm &gt;= 10 else \"0\"+str(mm)      return hh+\":\"+mm # print(mtime_to_hhmm(601))   def solution(n, t, m, timetable):     departs = sorted(nt_to_arr(n, t))     arrives = sorted(timetable_to_arr(timetable))      q = deque(arrives)      for idx, time in enumerate(departs):         cnt0 = 0         cnt = 0         if idx == len(departs)-1:             for stamp in q:                 if stamp &lt;= time:                     cnt0 += 1             if cnt0 &lt; m:                 return mtime_to_hhmm(time)                        while q:             now = q.popleft()             if now &lt;= time:                 cnt += 1                 if cnt == m:                     if idx == len(departs)-1:                         cnt == m-1                         return mtime_to_hhmm(now-1)                     break             else:                 q.appendleft(now)                 break   # ì •ë‹µ ë§ëŠ”ì§€ í™•ì¸ timetable = [\"08:00\", \"08:01\", \"08:02\", \"08:03\"] print(solution(1, 1, 5, timetable)) timetable = [\"09:10\", \"09:09\", \"08:00\"] print(solution(2, 10, 2, timetable))  timetable = [\"09:00\", \"09:00\", \"09:00\", \"09:00\"] print(solution(2, 1, 2, timetable))  timetable = [\"00:01\", \"00:01\", \"00:01\", \"00:01\", \"00:01\"] print(solution(1, 1, 5, timetable))  timetable = [\"23:59\"] print(solution(1, 1, 1, timetable))  timetable =[\"23:59\",\"23:59\", \"23:59\", \"23:59\", \"23:59\", \"23:59\", \"23:59\", \"23:59\", \"23:59\", \"23:59\", \"23:59\", \"23:59\", \"23:59\", \"23:59\", \"23:59\", \"23:59\"] print(solution(10, 60, 45, timetable))   2. Refactoring í•œ ì½”ë“œ   # ì…”í‹€ ë²„ìŠ¤ ì‹œê°„ ì–´ë ˆì´(ë¶„ ë‹¨ìœ„ ex) 09:00 =&gt; 540) def nt_to_arr(n, t):     deptart_times = []     for i in range(n):         deptart_times.append(540+i*t)     return deptart_times  # ë¬¸ìì—´ ì‹œê°„ =&gt; ë¶„ë‹¨ìœ„ ì‹œê°„ ë³€í™˜ def timetable_to_arr(timetable):     arrive_times = []      for stamp in timetable:         hh, mm = stamp.split(\":\")         arrive_times.append(60*int(hh) + int(mm))      return sorted(arrive_times)  # ë¶„ë‹¨ìœ„ ì‹œê°„ =&gt; ë¬¸ìì—´ ì‹œê°„ ë³€í™˜ def mtime_to_hhmm(minute):     hh, mm = divmod(minute, 60)      hh = str(hh) if hh &gt;= 10 else \"0\"+str(hh)     mm = str(mm) if mm &gt;= 10 else \"0\"+str(mm)      return hh+\":\"+mm  import heapq  def solution(n, t, m, timetable):     departs = nt_to_arr(n, t)     arrives = timetable_to_arr(timetable)      for dept_time in departs:         if dept_time == departs[-1]:             cnt = 0             for arrive_time in arrives:                 if arrive_time &gt; dept_time:                     break                 cnt += 1             if cnt &lt; m:                 return mtime_to_hhmm(dept_time)                   cnt = 0         while arrives:              if arrives[0] &gt; dept_time:                 break                      now = arrives.pop(0)             cnt += 1             if dept_time == departs[-1] and cnt == m:                 return mtime_to_hhmm(now-1)                          if m == cnt:                 break           # ì˜ˆì œ ì •ë‹µì´ ë§ëŠ”ì§€ í™•ì¸ timetable = [\"08:00\", \"08:01\", \"08:02\", \"08:03\"] print(solution(1, 1, 5, timetable))  timetable = [\"09:10\", \"09:09\", \"08:00\"] print(solution(2, 10, 2, timetable))  timetable = [\"09:00\", \"09:00\", \"09:00\", \"09:00\"] print(solution(2, 1, 2, timetable))  timetable = [\"00:01\", \"00:01\", \"00:01\", \"00:01\", \"00:01\"] print(solution(1, 1, 5, timetable))  timetable = [\"23:59\"] print(solution(1, 1, 1, timetable))  timetable =[\"23:59\",\"23:59\", \"23:59\", \"23:59\", \"23:59\", \"23:59\", \"23:59\", \"23:59\", \"23:59\", \"23:59\", \"23:59\", \"23:59\", \"23:59\", \"23:59\", \"23:59\", \"23:59\"] print(solution(10, 60, 45, timetable))   ","categories": ["PS"],
