        "excerpt":"ë¬¸ì œ   https://programmers.co.kr/learn/courses/30/lessons/43163?language=java   í’€ì´   1. ì²˜ìŒ ì‹œê°„ ì´ˆê³¼ê°€ ë‚œ ì½”ë“œ   import org.junit.jupiter.api.Test; import java.util.Arrays; import static org.junit.jupiter.api.Assertions.assertEquals;  class Solution1 {      static int ans = 51;      public int solution(String begin, String target, String[] words) {         int answer = 0;         if (Arrays.stream(words).anyMatch(x -&gt; x == target)) {             return 0;         }          dfs(words, begin, target, 0);         return ans == 51? 0: ans;     }      public static boolean oneDiff(String a, String b) {         int cnt = 0;          for (int i = 0; i &lt; a.length(); i++) {             if (a.charAt(i) != b.charAt(i)) {                 cnt++;             }         }          return cnt == 1 ? true : false;     }      public static void dfs(String[] word, String begin, String target, int cnt) {         if (begin.equals(target)) { // ëŒ€ë°•... //            System.out.println(\"check\");             if (cnt &lt; ans) { //                System.out.println(begin + target);                 ans = cnt;                 return;             }         } else if (cnt &gt; word.length) {             return;         }          for (int i = 0; i &lt; word.length; i++) {             if (oneDiff(begin, word[i])) {                 String[] newWord = new String[word.length - 1]; //                System.out.println(word.length - 1);                 for (int j = 0; j &lt; word.length; j++) {                     if (j &gt;= i &amp;&amp; j !=0) { //                        System.out.println(i + \" \" +j);                         newWord[j - 1] = word[j];                     } else {                         newWord[j] = word[j];                     }                 }                  dfs(word, word[i], target, cnt + 1); //                System.out.println(\"begin: \" + word[i] + \" target: \" + target);             }         }     }      @Test     public void ì •ë‹µ() {         String begin = new String(\"hit\");         String target = new String(\"cog\");         String[] words = {\"hot\", \"dot\", \"dog\", \"lot\", \"log\", \"cog\"};         assertEquals(4, solution(begin, target, words));          begin = \"hit\";         target = \"cog\";         words = new String[]{\"hot\", \"dot\", \"dog\", \"lot\", \"log\"};         ans = 0;         assertEquals(0, solution(begin, target, words));      }  }   2. ì •ë‹µ ì½”ë“œ   class Solution {     // ì•„ë˜ì™€ ê°™ì´ ê·¸ëƒ¥ ì „ì—­ë³€ìˆ˜ ì„ ì–¸í•´ì£¼ë©´ ë¨     int ans = 51;     boolean[] visited;      public int solution(String begin, String target, String[] words) {         visited = new boolean[words.length];          dfs(words, begin, target, 0);          return ans == 51 ? 0 : ans;     }      private void dfs(String[] words, String begin, String target, int cnt) {         if (begin.equals(target)) {             ans = ans &gt; cnt ? cnt : ans;         }           for (int i = 0; i &lt; words.length; i++) {             if (!visited[i] &amp;&amp; check(begin, words[i])) {                 visited[i] = true;                 dfs(words, words[i], target, cnt + 1);                 // ë‹¤ìŒ ì°¨ë¡€ì˜ dfsëŠ” í•´ë‹¹ nodeë¥¼ ë°©ë¬¸í•´ì•¼í•¨ìœ¼ë¡œ!                 visited[i] = false;             }         }     }      private boolean check(String begin, String word) {         int cnt = 0;          for (int i = 0; i &lt; word.length(); i++) {             if (begin.charAt(i) != word.charAt(i)) {                 cnt++;             }         }          return cnt == 1 ? true : false;     } }   ","categories": ["PS"],
